import React, { useState, useEffect } from 'react';
import './App.css'; // or the appropriate path to your CSS file

// Mock data for companies
const companies = [
    { name: 'Company A', industry: 'IA', website: 'WA', employees: [{ name: 'Employee A1', title: 'Title A1', linkedIn: 'LinkedIn A1' }] },
    { name: 'Company B', industry: 'IB', website: 'WB', employees: [{ name: 'Employee B1', title: 'Title B1', linkedIn: 'LinkedIn B1' }] },
    { name: 'Company C', industry: 'IC', website: 'WC', employees: [{ name: 'Employee C1', title: 'Title C1', linkedIn: 'LinkedIn C1' }] },
];

function SearchBox({ onSearchClick, searchTerm, onSearchTermChange }) {
    return (
        <div>
            <input type="text" value={searchTerm} onChange={onSearchTermChange} placeholder="Search..." />
            <button onClick={onSearchClick}>Search</button>
        </div>
    );
}


const CompanyList = ({ companies, onSelect }) => {
    return (
        <table>
            <thead>
                <tr>
                    <th>Company Name</th>
                    <th>Industry</th>
                    <th>Website</th>
                    <th>Employees</th>
                </tr>
            </thead>
            <tbody>
                {companies.map((company
                    , index) => (
                    <tr key={index} onClick={() => onSelect(company)}>
                        <td>{company.name}</td>
                        <td>{company.industry}</td>
                        <td><a href={company.website} target="_blank" rel="noopener noreferrer">{company.website}</a></td>
                        <td>
                            {company.employees.map((employee, eIndex) => (
                                <div key={eIndex}> {/* Using div to wrap each employee name to display them in block style */}
                                    <a href={employee.linkedIn} target="_blank" rel="noopener noreferrer">{employee.name}</a>
                                </div>
                            ))}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};


function App() {
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredCompanies, setFilteredCompanies] = useState([]);

    const handleSearch = () => {
        const filtered = companies.filter(company =>
            company.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            company.industry.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredCompanies(filtered);
    };

    return (
        <div>
            <h1>Inconsistent Contacts</h1>
            <SearchBox
                onSearchClick={handleSearch}
                searchTerm={searchTerm}
                onSearchTermChange={(e) => setSearchTerm(e.target.value)}
            />
            {!selectedCompany ? (
                <CompanyList companies={filteredCompanies} onSelect={setSelectedCompany} />
            ) : (
                <CompanyDetails company={selectedCompany} onBack={() => setSelectedCompany(null)} />
            )}
        </div>
    );
}

export default App;
